package Pregunta2;

public class Pregunta2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		System.out.println(i++);//imprime 1
		System.out.println(i); // imprime 2
		int j=1;
		System.out.println (++j);//imprime 2
		System.out.println (j);//imprime 2
	}
}
